mapper.delete:

delete from sc_estate_staff_rel
where estate_id = #{estateId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}

mapper.update:

update sc_estate_staff_rel
set create_time = #{createTime,jdbcType=TIMESTAMP},
  creator_id = #{creatorId,jdbcType=INTEGER},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where estate_id = #{estateId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}

mapper.select:

select estate_id, staff_id, create_time, creator_id, modify_time
from sc_estate_staff_rel
where estate_id = #{estateId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}
