mapper.delete:

delete from sc_party_activities
where id = #{id,jdbcType=INTEGER}
  and topic_id = #{topicId,jdbcType=INTEGER}
  and party_id = #{partyId,jdbcType=INTEGER}

mapper.select:

select id, topic_id, party_id, title, description, start_time, content, comment, 
duration, activity_status, create_time,  creator_id, creator_name, modify_time
from sc_party_activities
where id = #{id,jdbcType=INTEGER}
  and topic_id = #{topicId,jdbcType=INTEGER}
  and party_id = #{partyId,jdbcType=INTEGER}
