mapper.delete:

delete from sc_party_learn_files
where id = #{id,jdbcType=INTEGER}
  and activity_id = #{activityId,jdbcType=INTEGER}

mapper.select:

select id, activity_id, name, file_type, file_url, comment, activity_type, create_time, 
 creator_id, creator_name, modify_time
from sc_party_learn_files
where id = #{id,jdbcType=INTEGER}
  and activity_id = #{activityId,jdbcType=INTEGER}
