mapper.delete:

delete from sc_user_house_rel
where user_id = #{userId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}

mapper.update:

update sc_user_house_rel
set owner_rel = #{ownerRel,jdbcType=TINYINT},
  valid_time = #{validTime,jdbcType=DATE},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  creator_id = #{creatorId,jdbcType=INTEGER},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}

mapper.select:

select user_id, house_id, owner_rel, valid_time, create_time, creator_id, modify_time
from sc_user_house_rel
where user_id = #{userId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
